    xk_re: OUT std_logic_VECTOR(20 downto 0);
    xk_im: OUT std_logic_VECTOR(20 downto 0);
    xn_index: OUT std_logic_VECTOR(11 downto 0);
    xk_index: OUT std_logic_VECTOR(11 downto 0);
    rfd: OUT std_logic;
    busy: OUT std_logic;
    dv: OUT std_logic;
    edone: OUT std_logic;
    done: OUT std_logic);
end component;



